def add_level(level_num,img):
    level_n=pygame.image.load(img)
    level_n_pg=pygame.surfarray.array3d(level_n).tolist()
    height, width = len(level_n_pg), len(level_n_pg[0])
    level_n=[[[] for _ in range(width)]for _ in range(height)]
    for i in range(len(level_n_pg)):
        for j in range(len(level_n_pg[i])):
            level_n[j][i]=level_n_pg[i][j]
    width, height = len(level_n_pg), len(level_n_pg[0])
    level[level_num]={}
    level[level_num]['target']=level_n
    import random
    a=[[-1 for _ in range(width)] for i in range(height)]
    a1=[i for i in range(width+height)]
    random.shuffle(a1)
    paint_hang=[[[0,0,0] for _ in range(width)]for _ in range(height)]
    paint_lie=[[[0,0,0] for _ in range(width)]for _ in range(height)]
    for i in a1:
        if i<height:
            a[i]=[0 for i in range(width)]
        else:
            for j in range(height):
                a[j][i-height]=1
    for i in range(len(a)):
        for j in range(len(a[i])):
            if a[i][j]:
                paint_hang[i][j]=level_n[i][j]
                paint_lie[i][j] = random.choice(random.choice(level_n))
            else:
                paint_lie[i][j]=level_n[i][j]
                paint_hang[i][j] = random.choice(random.choice(level_n))
    level[level_num]['paint_hang']=paint_hang
    level[level_num]['paint_lie']=paint_lie
add_level(7,"image/level/mc金苹果.png")
add_level(8,"image/level/末影珍珠.png")
add_level(9,"image/level/img.png")
